appsmith.onReady(() => {
  new Vue({
    el: "#app",
    data() {
      return {
        mapInstance: null,
        position: {
          lat: 15.115466,
          lng: 108.804808
        },
        // Vị trí hiện tại
        locationsList: appsmith.model.map || [] // Danh sách các địa điểm
      };
    },
    async mounted() {
      this.initMap();
      this.hienThiDanhSachDiaChi();
    },
    methods: {
      // Hàm khởi tạo bản đồ
      initMap() {
        this.mapInstance = new DTUAppsmithMap("map", {
          center: [this.position.lat, this.position.lng],
          zoom: 10 // Mức thu phóng
        });

        // Hiển thị vị trí hiện tại
        this.mapInstance.capNhatViTriHienTai(this.position.lat, this.position.lng, "Vị trí hiện tại", "Bạn đang ở đây", "#FF5733");
      },
      // Hiển thị danh sách các địa điểm
      hienThiDanhSachDiaChi() {
        this.locationsList.forEach(value => {
          // Hiển thị địa điểm trên bản đồ
          this.mapInstance.taoDiaDiem(value.toa_do_x,
          // Vĩ độ
          value.toa_do_y,
          // Kinh độ
          `${value.ten_nha_tai_tro} cung cấp ${value.loai_nguon_luc}`,
          // Tiêu đề
          value.dia_chi || "Không có địa chỉ",
          // Nội dung popup
          value.loai_icon || "fas fa-map-marker-alt",
          // Icon
          "#003C71" // Màu icon
          );

          // Vẽ vùng tròn dựa trên logic mức độ
          this.drawCircleBasedOnLevel(value);
        });
      },
      // Hàm vẽ vùng tròn dựa trên mức độ (mặc định là màu xanh)
      drawCircleBasedOnLevel(location) {
        const radius = 100; // Bán kính (mét)
        this.mapInstance.veVungTron(location.toa_do_x, location.toa_do_y, radius, "#00FF00" // Màu vùng tròn
        );
      },
      // Hàm reset (nếu cần)
      reset() {
        appsmith.triggerEvent("onReset");
      },
      // Ghi lại vị trí (nếu cần)
      showPosition(position) {
        console.log(position);
      }
    }
  });
});

