appsmith.onReady(() => {
  new Vue({
    el: "#app",
    data() {
      return {
        mapInstance: null,
        position: appsmith.model.map,
        locationsList: [{
          lat: 16.0464319,
          // Khoảng 3km về phía Nam
          lng: 108.2326082,
          title: "Địa điểm 1",
          content: "Cách 3km về phía Nam",
          iconClass: "fas fa-building",
          iconColor: "#FF0000"
        }, {
          lat: 16.0899319,
          // Khoảng 3km về phía Bắc-Đông
          lng: 108.2496082,
          title: "Địa điểm 2",
          content: "Cách 3km về phía Bắc-Đông",
          iconClass: "fas fa-university",
          iconColor: "#00FF00"
        }]
      };
    },
    async mounted() {
      this.initMap();
      appsmith.onModelChange("map", () => {
        this.capNhatViTriMoi();
      });
    },
    methods: {
      initMap() {
        this.mapInstance = new DTUAppsmithMap("map", {
          center: [this.position.lat, this.position.lng],
          zoom: 16
        });
        this.mapInstance.capNhatViTriHienTai(this.position.lat, this.position.lng);
        // this.locationsList.forEach((value, index) => {
        // var khoang_cach = this.mapInstance.tinhKhoangCachDenDiem(value.lat, value.lng);
        // value.content = "Cách "+ khoang_cach +"km so với vị trí hiện tại";
        // this.mapInstance.veDuongDi(this.position.lat, this.position.lng, value.lat, value.lng);
        // // this.mapInstance.taoDiaDiem(value.lat, value.lng,  value.title, value.content, value.iconClass, value.iconColor)
        // })
      }
    }
  });
  const vm = app.mount('#app');
  appsmith.onModelChange(() => {
    vm.updateUsers(appsmith.model.map); // Call the method to update data reactively
  });
});

